const fs = require('fs');
var someArray = [];
var someNum = 0;
var globalData;
var interval;

function fetchMessages() {
    if (someNum != someArray.length) {
        var channelToFetch = client.channels.get(someArray[someNum]);
        if (channelToFetch != null) {
           channelToFetch.fetchMessages({ limit: 100 });
           console.log(`Fetched messages in channel id ${someArray[someNum]}`);
           someNum = someNum + 1;
        } else {
           delete globalData[someArray[someNum]];
           fs.writeFile("./resources/serverRoleMenu.json", JSON.stringify(globalData), function(err) {
               if(err) {
                   return console.log(err);
               }
           }); 
        }
    } else {
       console.log('Finished fetching messages from all logged servers.')
       clearInterval(interval);
    }
   }

